function determineClaimTruth(claimText) {
  // Table data
  let tableData = [
    ["vaea anitoni", "1992 - 2000", 44, 26, 0, 0, 0],
    ["paul emerick", "2003 - 2012", 49, 17, 0, 0, 0],
    ["todd clever", "2003 -", 51, 11, 0, 0, 0],
    ["philip eloff", "2000 - 2007", 34, 10, 0, 0, 0],
    ["takudzwa ngwenya", "2007 -", 27, 10, 0, 0, 0],
    ["chris wyles", "2007 -", 35, 10, 14, 22, 1],
    ["david fee", "2002 - 2005", 28, 9, 0, 0, 0],
    ["mike hercus", "2002 - 2009", 45, 9, 90, 76, 4],
    ["riaan van zyl", "2003 - 2004", 12, 9, 0, 0, 0]
  ];

  // Find rows where span not yet finished
  let incompleteSpans = tableData.filter((row, index) => {
    if (index === 0 || index === 8) return false;
    if (row[2] < row[1].split('-')[1]) {
      console.log("Not done: ", row);
      return true;
    }
    return false;
  });

  // Check claim
  let answer = incompleteSpans.length >= 3;

  // Print result
  let relevantCells = [];
  if (answer) {
    for (let i = 0; i < tableData.length; i++) {
      if (i === 0 || i === 8) continue;
      if (!incompleteSpans.find((row) => row[0] === tableData[i][0])) {
        relevantCells.push({ "row_index": i, "column_name": "player" });
      }
    }
  }

  return { answer: answer ? 'TRUE' : 'FALSE', relevant_cells: relevantCells };
}

// Example usage:
let claimText = `"3 of the 9 player have not yet finish their span of play on the united state national rugby union team"`;
