var table = {
    "columns": ["tournament", "wins", "top - 5", "top - 10", "top - 25", "events", "cuts made"],
    "data": [
        ["masters tournament", 0, 1, 2, 4, 4, 4],
        ["us open", 0, 2, 3, 4, 6, 5],
        ["the open championship", 1, 2, 2, 2, 3, 3],
        ["pga championship", 0, 0, 1, 2, 5, 4],
        ["totals", 1, 5, 8, 12, 18, 16]
    ]
};

function evaluateClaim() {
  var answer = 'FALSE';
  for (var row = 0; row < table.data.length; row++) {
      if(table.data[row][2] == 1 && table.data[row][1] > 0) {
        answer = "TRUE";
        break;
      }
  }

  var relevantCells = [];
  for (var colIndex = 0; colIndex < table.columns.length; colIndex++) {
    if(answer === 'TRUE') {

      for (var row = 0; row < table.data.length; row++) {
        if(table.data[row][colIndex] == 1 && table.data[row][2] == 1 && table.data[row][1] > 0) {
          relevantCells.push({row_index: row, column_name: table.columns[colIndex]});
        }
      }
    } else {

      for (var row = 0; row < table.data.length; row++) {
        if(table.data[row][colIndex] == 1 && table.data[row][2] >= 1) {
          relevantCells.push({row_index: row, column_name: table.columns[colIndex]});
        }
      }
    }
  }

  return {"answer": answer.toUpperCase(), "relevant_cells": relevantCells};
}

console.log(evaluateClaim());
