import json
from dataclasses import dataclass

@dataclass
class Cell:
    row_index: int
    column_name: str

@dataclass
class Result:
    answer: str
    relevant_cells: list[Cell]

def evaluate_claim(data):
    # First, we need to find the first game of the World Cup in France.
    world_cup_games = [game for game in data["data"] if game[-1] == "world cup"]
    if not world_cup_games:
        return Result(answer="FALSE", relevant_cells=[])

    first_world_cup_game = world_cup_games[0]

    # Check that the score of both teams is reported.
    if any(cell['report'] for cell in [first_world_cup_game[i] for i in range(4)]):
        return Result(answer="FALSE", relevant_cells=Cell(row_index=6, column_name="score"))

    else:
        return Result(answer="TRUE", relevant_cells=[])

