      { year: '2005', award: 'drama desk award', category: 'outstanding costume design', nominee: 'william ivey long', result: 'nominated' }
    ];

    var jerryMitchellData = table.filter(row => row.award === "tony award" && row.category === "best choreography").map((row, index) => {
