    }).concat(table.filter(row => row.award === "drama desk award" && row.category === "outstanding choreography").map((row, index) => {
        return { row_index: index + table.length / 2, column_name: "won" };
    }));

    if (jerryMitchellData.length >= 2) {
      return JSON.stringify({ answer: "TRUE", relevant_cells:jerryMitchellData });
    } else {
      return JSON.stringify({ answer: "FALSE", relevant_cells:[] });
    }
}

