function isLowestVisitorScore(table, rowIndex) {
  const row = table[rowIndex];
  if (!row.score) return false;
  let lowestScore = Infinity;
  for (const cell of table) {
    if (cell.visitor === 'Detroit') continue;
    if (cell.score < lowestScore) lowestScore = cell.score;
  }
  return lowestScore > row.score;
}

function getRelevantCells(rowIndex, columnNames) {
