def get_cells(data):
    team_counts = {}
    for i in range(len(data['team'])):
        if data['team'][i] not in team_counts:
            team_counts[data['team'][i]] = []
        team_counts[data['team'][i']].append(i)

    for key, value in data.items():
        if len(value) == 1 or key != 'difference':
            continue
        counts = {}
        for i in range(len(data['team'])):
            counts[value[i][0]] = value[i][1]

        same_diff_teams = [team for team, diff in counts.items() if diff in counts.values()]
        if len(same_diff_teams) > 1:
            relevant_cells = [{"row_index": i, "column_name": key} for i, key in enumerate(value)]
            return {"answer": "TRUE", "relevant_cells": relevant_cells}
    return {"answer": "FALSE", "relevant_cells": []}

