function analyzeClaim() {
    // Read table from HTML
    const table = document.querySelector('table');

    // Initialize counters for home wins and losses
    let homeWins = 0;
    let homeLosses = 0;

    // Loop through each row in the table
    Array.from(table.rows).forEach((row, rowIndex) => {
        if (rowIndex === 0) return; // Skip header row

        const cells = row.cells;
        if (cells[2].textContent.includes(' - ') && cells[1].textContent.toLowerCase() === 'nuggets') { // Check if home team is nuggets and it's a win
            homeWins++;
        } else if (cells[0].textContent.toLowerCase().includes('december 2007') && cells[3].textContent.toLowerCase() === 'nuggets' && cells[2].textContent.includes('-')) { // Check if home team is nuggets and it's a loss
            homeLosses++;
        }
    });

    const relevantCells = [
        {rowIndex: 0, columnName: "score"},
        {rowIndex: 1, columnName: "record"},
        {rowIndex: 2, columnName: "leading scorer"},
        {rowIndex: 3, columnName: "attendance"}
    ];

    // Compare counts and output result
    if (homeWins > homeLosses) {
        return JSON.stringify({
            chain_of_thought:
                `Analyzed table to determine Denver Nuggets' performance in December 2007. The claim 'the denver nuggets lose more home game in december 2007 than they win' was verified based on the number of wins and losses.",
            answer: "TRUE",
            relevantCells
        });
    } else {
        return JSON.stringify({
            chain_of_thought:
                `Analyzed table to determine Denver Nuggets' performance in December 2007. The claim 'the denver nuggets lose more home game in december 2007 than they win' was verified based on the number of wins and losses. It was found that the team does not lose more home games than it wins.`,
            answer: "FALSE",
            relevantCells
        });
    }
}

console.log(analyzeClaim());
