const facilityManagementRow = data.find((row) => row.industry === "facility management");

if (manufacturingRow && facilityManagementRow) {
  // Compare the number of employees in both industries
  if (manufacturingRow.employees < facilityManagementRow.employees) {
    console.log("The company in the manufacturing industry has fewer employees.");
